import{_ as t,a}from"./app.377d56da.js";var e="/document/assets/jvm_classlaoder_architecture.4a0c68ff.svg";const l={},d=a('<h1 id="\u7C7B\u52A0\u8F7D\u5668" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u52A0\u8F7D\u5668" aria-hidden="true">#</a> \u7C7B\u52A0\u8F7D\u5668</h1><p>\u7C7B\u52A0\u8F7D\u5668\u662F Java \u8FD0\u884C\u65F6\u73AF\u5883\uFF08Java Runtime Environment\uFF09\u7684\u4E00\u90E8\u5206\uFF0C\u8D1F\u8D23\u52A8\u6001\u52A0\u8F7D Java \u7C7B\u5230 Java \u865A\u62DF\u673A\u7684\u5185\u5B58\u7A7A\u95F4\u4E2D\u3002<strong>\u7C7B\u901A\u5E38\u662F\u6309\u9700\u52A0\u8F7D\uFF0C\u5373\u7B2C\u4E00\u6B21\u4F7F\u7528\u8BE5\u7C7B\u65F6\u624D\u52A0\u8F7D\u3002</strong> \u7531\u4E8E\u6709\u4E86\u7C7B\u52A0\u8F7D\u5668\uFF0CJava \u8FD0\u884C\u65F6\u7CFB\u7EDF\u4E0D\u9700\u8981\u77E5\u9053\u6587\u4EF6\u4E0E\u6587\u4EF6\u7CFB\u7EDF\u3002\u6BCF\u4E2A Java \u7C7B\u5FC5\u987B\u7531\u67D0\u4E2A\u7C7B\u52A0\u8F7D\u5668\u88C5\u5165\u5230\u5185\u5B58\u3002</p><p><img src="'+e+'" alt="jvm_classloader_architecture"></p><p>\u7C7B\u88C5\u8F7D\u5668\u9664\u4E86\u8981\u5B9A\u4F4D\u548C\u5BFC\u5165\u4E8C\u8FDB\u5236 class \u6587\u4EF6\u5916\uFF0C\u8FD8\u5FC5\u987B\u8D1F\u8D23\u9A8C\u8BC1\u88AB\u5BFC\u5165\u7C7B\u7684\u6B63\u786E\u6027\uFF0C\u4E3A\u53D8\u91CF\u5206\u914D\u521D\u59CB\u5316\u5185\u5B58\uFF0C\u4EE5\u53CA\u5E2E\u52A9\u89E3\u6790\u7B26\u53F7\u5F15\u7528\u3002\u8FD9\u4E9B\u52A8\u4F5C\u5FC5\u987B\u4E25\u683C\u6309\u4E00\u4E0B\u987A\u5E8F\u5B8C\u6210\uFF1A</p><ol><li><strong>\u88C5\u8F7D</strong>\uFF1A\u67E5\u627E\u5E76\u88C5\u8F7D\u7C7B\u578B\u7684\u4E8C\u8FDB\u5236\u6570\u636E\u3002</li><li><strong>\u94FE\u63A5</strong>\uFF1A\u6267\u884C\u9A8C\u8BC1\u3001\u51C6\u5907\u4EE5\u53CA\u89E3\u6790(\u53EF\u9009) - <strong>\u9A8C\u8BC1</strong>\uFF1A\u786E\u4FDD\u88AB\u5BFC\u5165\u7C7B\u578B\u7684\u6B63\u786E\u6027 - <strong>\u51C6\u5907</strong>\uFF1A\u4E3A\u7C7B\u53D8\u91CF\u5206\u914D\u5185\u5B58\uFF0C\u5E76\u5C06\u5176\u521D\u59CB\u5316\u4E3A\u9ED8\u8BA4\u503C\u3002 - <strong>\u89E3\u6790</strong>\uFF1A\u628A\u7C7B\u578B\u4E2D\u7684\u7B26\u53F7\u5F15\u7528\u8F6C\u6362\u4E3A\u76F4\u63A5\u5F15\u7528\u3002</li><li><strong>\u521D\u59CB\u5316</strong>\uFF1A\u628A\u7C7B\u53D8\u91CF\u521D\u59CB\u5316\u4E3A\u6B63\u786E\u7684\u521D\u59CB\u503C\u3002</li></ol><h2 id="\u88C5\u8F7D" tabindex="-1"><a class="header-anchor" href="#\u88C5\u8F7D" aria-hidden="true">#</a> \u88C5\u8F7D</h2><h3 id="\u7C7B\u52A0\u8F7D\u5668\u5206\u7C7B" tabindex="-1"><a class="header-anchor" href="#\u7C7B\u52A0\u8F7D\u5668\u5206\u7C7B" aria-hidden="true">#</a> \u7C7B\u52A0\u8F7D\u5668\u5206\u7C7B</h3><p>\u5728Java\u865A\u62DF\u673A\u4E2D\u5B58\u5728\u591A\u4E2A\u7C7B\u88C5\u8F7D\u5668\uFF0CJava\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u4F7F\u7528\u4E24\u79CD\u7C7B\u88C5\u8F7D\u5668\uFF1A</p><ul><li><strong>Bootstrap ClassLoader</strong>\uFF1A\u6B64\u88C5\u8F7D\u5668\u662F Java \u865A\u62DF\u673A\u5B9E\u73B0\u7684\u4E00\u90E8\u5206\u3002\u7531\u539F\u751F\u4EE3\u7801\uFF08\u5982C\u8BED\u8A00\uFF09\u7F16\u5199\uFF0C\u4E0D\u7EE7\u627F\u81EA <code>java.lang.ClassLoader</code> \u3002\u8D1F\u8D23\u52A0\u8F7D\u6838\u5FC3 Java \u5E93\uFF0C\u542F\u52A8\u7C7B\u88C5\u8F7D\u5668\u901A\u5E38\u4F7F\u7528\u67D0\u79CD\u9ED8\u8BA4\u7684\u65B9\u5F0F\u4ECE\u672C\u5730\u78C1\u76D8\u4E2D\u52A0\u8F7D\u7C7B\uFF0C\u5305\u62EC Java API\u3002</li><li><strong>Extention Classloader</strong>\uFF1A\u7528\u6765\u5728<code>&lt;JAVA_HOME&gt;/jre/lib/ext</code> ,\u6216 <code>java.ext.dirs</code> \u4E2D\u6307\u660E\u7684\u76EE\u5F55\u4E2D\u52A0\u8F7D Java \u7684\u6269\u5C55\u5E93\u3002 Java \u865A\u62DF\u673A\u7684\u5B9E\u73B0\u4F1A\u63D0\u4F9B\u4E00\u4E2A\u6269\u5C55\u5E93\u76EE\u5F55\u3002</li><li><strong>Application Classloader</strong>\uFF1A\u6839\u636E Java\u5E94\u7528\u7A0B\u5E8F\u7684\u7C7B\u8DEF\u5F84\uFF08 <code>java.class.path</code> \u6216 <code>CLASSPATH</code> \u73AF\u5883\u53D8\u91CF\uFF09\u6765\u52A0\u8F7D Java \u7C7B\u3002\u4E00\u822C\u6765\u8BF4\uFF0CJava \u5E94\u7528\u7684\u7C7B\u90FD\u662F\u7531\u5B83\u6765\u5B8C\u6210\u52A0\u8F7D\u7684\u3002\u53EF\u4EE5\u901A\u8FC7 <code>ClassLoader.getSystemClassLoader()</code> \u6765\u83B7\u53D6\u5B83\u3002</li><li><strong>\u81EA\u5B9A\u4E49\u7C7B\u52A0\u8F7D\u5668</strong>\uFF1A\u53EF\u4EE5\u901A\u8FC7\u7EE7\u627F <code>java.lang.ClassLoader</code> \u7C7B\u7684\u65B9\u5F0F\u5B9E\u73B0\u81EA\u5DF1\u7684\u7C7B\u52A0\u8F7D\u5668\uFF0C\u4EE5\u6EE1\u8DB3\u4E00\u4E9B\u7279\u6B8A\u7684\u9700\u6C42\u800C\u4E0D\u9700\u8981\u5B8C\u5168\u4E86\u89E3 Java \u865A\u62DF\u673A\u7684\u7C7B\u52A0\u8F7D\u7684\u7EC6\u8282\u3002</li></ul><h3 id="\u5168\u76D8\u8D1F\u8D23\u53CC\u4EB2\u59D4\u6258\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#\u5168\u76D8\u8D1F\u8D23\u53CC\u4EB2\u59D4\u6258\u673A\u5236" aria-hidden="true">#</a> \u5168\u76D8\u8D1F\u8D23\u53CC\u4EB2\u59D4\u6258\u673A\u5236</h3><p>\u5728\u4E00\u4E2A JVM \u7CFB\u7EDF\u4E2D\uFF0C\u81F3\u5C11\u6709 3 \u79CD\u7C7B\u52A0\u8F7D\u5668\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u7C7B\u52A0\u8F7D\u5668\u5982\u4F55\u914D\u5408\u5DE5\u4F5C\uFF1F\u5728 JVM \u79CD\u7C7B\u52A0\u8F7D\u5668\u901A\u8FC7 <strong>\u5168\u76D8\u8D1F\u8D23\u53CC\u4EB2\u59D4\u6258\u673A\u5236</strong> \u6765\u534F\u8C03\u7C7B\u52A0\u8F7D\u5668\u3002</p><ul><li><strong>\u5168\u76D8\u8D1F\u8D23</strong>\uFF1A\u6307\u5F53\u4E00\u4E2A <code>ClassLoader</code> \u88C5\u8F7D\u4E00\u4E2A\u7C7B\u7684\u65F6\uFF0C\u9664\u975E\u663E\u5F0F\u5730\u4F7F\u7528\u53E6\u4E00\u4E2A <code>ClassLoader</code> \uFF0C\u8BE5\u7C7B\u6240\u4F9D\u8D56\u53CA\u5F15\u7528\u7684\u7C7B\u4E5F\u7531\u8FD9\u4E2A <code>ClassLoader</code> \u8F7D\u5165\u3002</li><li><strong>\u53CC\u4EB2\u59D4\u6258\u673A\u5236</strong>\uFF1A\u6307\u5148\u59D4\u6258\u7236\u88C5\u8F7D\u5668\u5BFB\u627E\u76EE\u6807\u7C7B\uFF0C\u53EA\u6709\u5728\u627E\u4E0D\u5230\u7684\u60C5\u51B5\u4E0B\u624D\u4ECE\u81EA\u5DF1\u7684\u7C7B\u8DEF\u5F84\u4E2D\u67E5\u627E\u5E76\u88C5\u8F7D\u76EE\u6807\u7C7B\u3002</li></ul><p>\u5168\u76D8\u8D1F\u8D23\u53CC\u4EB2\u59D4\u6258\u673A\u5236\u53EA\u662F Java \u63A8\u8350\u7684\u673A\u5236\uFF0C\u5E76\u4E0D\u662F\u5F3A\u5236\u7684\u673A\u5236\u3002\u5B9E\u73B0\u81EA\u5DF1\u7684\u7C7B\u52A0\u8F7D\u5668\u65F6\uFF0C\u5982\u679C\u60F3\u4FDD\u6301\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\uFF0C\u5C31\u5E94\u8BE5\u91CD\u5199 <code>findClass(name)</code> \u65B9\u6CD5\uFF1B\u5982\u679C\u60F3\u7834\u574F\u53CC\u4EB2\u59D4\u6D3E\u6A21\u578B\uFF0C\u53EF\u4EE5\u91CD\u5199 <code>loadClass(name)</code> \u65B9\u6CD5\u3002</p><h3 id="\u88C5\u8F7D\u5165\u53E3" tabindex="-1"><a class="header-anchor" href="#\u88C5\u8F7D\u5165\u53E3" aria-hidden="true">#</a> \u88C5\u8F7D\u5165\u53E3</h3><p>\u6240\u6709Java\u865A\u62DF\u673A\u5B9E\u73B0\u5FC5\u987B\u5728\u6BCF\u4E2A\u7C7B\u6216\u63A5\u53E3\u9996\u6B21\u4E3B\u52A8\u4F7F\u7528\u65F6\u521D\u59CB\u5316\u3002\u4EE5\u4E0B\u516D\u79CD\u60C5\u51B5\u7B26\u5408\u4E3B\u52A8\u4F7F\u7528\u7684\u8981\u6C42\uFF1A</p><ul><li>\u5F53\u521B\u5EFA\u67D0\u4E2A\u7C7B\u7684\u65B0\u5B9E\u4F8B\u65F6(new\u3001\u53CD\u5C04\u3001\u514B\u9686\u3001\u5E8F\u5217\u5316)</li><li>\u8C03\u7528\u67D0\u4E2A\u7C7B\u7684\u9759\u6001\u65B9\u6CD5</li><li>\u4F7F\u7528\u67D0\u4E2A\u7C7B\u6216\u63A5\u53E3\u7684\u9759\u6001\u5B57\u6BB5\uFF0C\u6216\u5BF9\u8BE5\u5B57\u6BB5\u8D4B\u503C(\u7528final\u4FEE\u9970\u7684\u9759\u6001\u5B57\u6BB5\u9664\u5916\uFF0C\u5B83\u88AB\u521D\u59CB\u5316\u4E3A\u4E00\u4E2A\u7F16\u8BD1\u65F6\u5E38\u91CF\u8868\u8FBE\u5F0F)</li><li>\u5F53\u8C03\u7528Java API\u7684\u67D0\u4E9B\u53CD\u5C04\u65B9\u6CD5\u65F6\u3002</li><li>\u521D\u59CB\u5316\u67D0\u4E2A\u7C7B\u7684\u5B50\u7C7B\u65F6\u3002</li><li>\u5F53\u865A\u62DF\u673A\u542F\u52A8\u65F6\u88AB\u6807\u660E\u4E3A\u542F\u52A8\u7C7B\u7684\u7C7B\u3002</li></ul><p>\u9664\u4EE5\u4E0A\u516D\u79CD\u60C5\u51B5\uFF0C\u6240\u6709\u5176\u4ED6\u4F7F\u7528Java\u7C7B\u578B\u7684\u65B9\u5F0F\u90FD\u662F\u88AB\u52A8\u7684\uFF0C\u5B83\u4EEC\u4E0D\u4F1A\u5BFC\u81F4Java\u7C7B\u578B\u7684\u521D\u59CB\u5316\u3002</p><p>\u5BF9\u4E8E\u63A5\u53E3\u6765\u8BF4\uFF0C\u53EA\u6709\u5728\u67D0\u4E2A\u63A5\u53E3\u58F0\u660E\u7684\u975E\u5E38\u91CF\u5B57\u6BB5\u88AB\u4F7F\u7528\u65F6\uFF0C\u8BE5\u63A5\u53E3\u624D\u4F1A\u521D\u59CB\u5316\uFF0C\u800C\u4E0D\u4F1A\u56E0\u4E3A\u4E8B\u5148\u8FD9\u4E2A\u63A5\u53E3\u7684\u5B50\u63A5\u53E3\u6216\u7C7B\u8981\u521D\u59CB\u5316\u800C\u88AB\u521D\u59CB\u5316\u3002</p><p><strong>\u7236\u7C7B\u9700\u8981\u5728\u5B50\u7C7B\u521D\u59CB\u5316\u4E4B\u524D\u88AB\u521D\u59CB\u5316</strong>\u3002\u5F53\u5B9E\u73B0\u4E86\u63A5\u53E3\u7684\u7C7B\u88AB\u521D\u59CB\u5316\u7684\u65F6\u5019\uFF0C\u4E0D\u9700\u8981\u521D\u59CB\u5316\u7236\u63A5\u53E3\u3002\u7136\u800C\uFF0C\u5F53\u5B9E\u73B0\u4E86\u7236\u63A5\u53E3\u7684\u5B50\u7C7B(\u6216\u8005\u662F\u6269\u5C55\u4E86\u7236\u63A5\u53E3\u7684\u5B50\u63A5\u53E3)\u88AB\u88C5\u8F7D\u65F6\uFF0C\u7236\u63A5\u53E3\u4E5F\u8981\u88AB\u88C5\u8F7D\u3002(\u53EA\u662F\u88AB\u88C5\u8F7D\uFF0C\u6CA1\u6709\u521D\u59CB\u5316)</p><h2 id="\u9A8C\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u9A8C\u8BC1" aria-hidden="true">#</a> \u9A8C\u8BC1</h2><p>\u786E\u8BA4\u88C5\u8F7D\u540E\u7684\u7C7B\u578B\u7B26\u5408Java\u8BED\u8A00\u7684\u8BED\u4E49\uFF0C\u5E76\u4E14\u4E0D\u4F1A\u5371\u53CA\u865A\u62DF\u673A\u7684\u5B8C\u6574\u6027\u3002</p><ul><li><strong>\u88C5\u8F7D\u65F6\u9A8C\u8BC1</strong>\uFF1A\u68C0\u67E5\u4E8C\u8FDB\u5236\u6570\u636E\u4EE5\u786E\u4FDD\u6570\u636E\u5168\u90E8\u662F\u9884\u671F\u683C\u5F0F\u3001\u786E\u4FDD\u9664 Object \u4E4B\u5916\u7684\u6BCF\u4E2A\u7C7B\u90FD\u6709\u7236\u7C7B\u3001\u786E\u4FDD\u8BE5\u7C7B\u7684\u6240\u6709\u7236\u7C7B\u90FD\u5DF2\u7ECF\u88AB\u88C5\u8F7D\u3002</li><li><strong>\u6B63\u5F0F\u9A8C\u8BC1\u9636\u6BB5</strong>\uFF1A\u68C0\u67E5 final \u7C7B\u4E0D\u80FD\u6709\u5B50\u7C7B\u3001\u786E\u4FDD final \u65B9\u6CD5\u4E0D\u88AB\u8986\u76D6\u3001\u786E\u4FDD\u5728\u7C7B\u578B\u548C\u8D85\u7C7B\u578B\u4E4B\u95F4\u6CA1\u6709\u4E0D\u517C\u5BB9\u7684\u65B9\u6CD5\u58F0\u660E(\u6BD4\u5982\u62E5\u6709\u4E24\u4E2A\u540D\u5B57\u76F8\u540C\u7684\u65B9\u6CD5\uFF0C\u53C2\u6570\u5728\u6570\u91CF\u3001\u987A\u5E8F\u3001\u7C7B\u578B\u4E0A\u90FD\u76F8\u540C\uFF0C\u4F46\u8FD4\u56DE\u7C7B\u578B\u4E0D\u540C)\u3002</li><li><strong>\u7B26\u53F7\u5F15\u7528\u7684\u9A8C\u8BC1</strong>\uFF1A\u5F53\u865A\u62DF\u673A\u641C\u5BFB\u4E00\u4E2A\u88AB\u7B26\u53F7\u5F15\u7528\u7684\u5143\u7D20(\u7C7B\u578B\u3001\u5B57\u6BB5\u6216\u65B9\u6CD5)\u65F6\uFF0C\u5FC5\u987B\u9996\u5148\u786E\u8BA4\u8BE5\u5143\u7D20\u5B58\u5728\u3002\u5982\u679C\u865A\u62DF\u673A\u53D1\u73B0\u5143\u7D20\u5B58\u5728\uFF0C\u5219\u5FC5\u987B\u8FDB\u4E00\u6B65\u68C0\u67E5\u5F15\u7528\u7C7B\u578B\u6709\u8BBF\u95EE\u8BE5\u5143\u7D20\u7684\u6743\u9650\u3002</li></ul><h2 id="\u51C6\u5907" tabindex="-1"><a class="header-anchor" href="#\u51C6\u5907" aria-hidden="true">#</a> \u51C6\u5907</h2><p>\u5728\u51C6\u5907\u9636\u6BB5\uFF0CJava\u865A\u62DF\u673A\u4E3A\u7C7B\u53D8\u91CF\u5206\u914D\u5185\u5B58\uFF0C<strong>\u8BBE\u7F6E\u9ED8\u8BA4\u521D\u59CB\u503C</strong>\u3002\u4F46\u5728\u5230\u5230\u521D\u59CB\u5316\u9636\u6BB5\u4E4B\u524D\uFF0C\u7C7B\u53D8\u91CF\u90FD\u6CA1\u6709\u88AB\u521D\u59CB\u5316\u4E3A\u771F\u6B63\u7684\u521D\u59CB\u503C\u3002</p><table><thead><tr><th style="text-align:left;">\u7C7B\u578B</th><th style="text-align:left;">\u9ED8\u8BA4\u503C</th></tr></thead><tbody><tr><td style="text-align:left;">int</td><td style="text-align:left;">0</td></tr><tr><td style="text-align:left;">long</td><td style="text-align:left;">0L</td></tr><tr><td style="text-align:left;">short</td><td style="text-align:left;">(short)0</td></tr><tr><td style="text-align:left;">char</td><td style="text-align:left;">&#39;\\u0000&#39;</td></tr><tr><td style="text-align:left;">byte</td><td style="text-align:left;">(byte)0</td></tr><tr><td style="text-align:left;">blooean</td><td style="text-align:left;">false</td></tr><tr><td style="text-align:left;">float</td><td style="text-align:left;">0.0f</td></tr><tr><td style="text-align:left;">double</td><td style="text-align:left;">0.0d</td></tr><tr><td style="text-align:left;">reference</td><td style="text-align:left;">null</td></tr></tbody></table><h2 id="\u89E3\u6790" tabindex="-1"><a class="header-anchor" href="#\u89E3\u6790" aria-hidden="true">#</a> \u89E3\u6790</h2><p>\u89E3\u6790\u7684\u8FC7\u7A0B\u5C31\u662F\u5728\u7C7B\u578B\u7684\u5E38\u91CF\u6C60\u603B\u5BFB\u627E\u7C7B\u3001\u63A5\u53E3\u3001\u5B57\u6BB5\u548C\u65B9\u6CD5\u7684\u7B26\u53F7\u5F15\u7528\uFF0C<strong>\u628A\u8FD9\u4E9B\u7B26\u53F7\u5F15\u7528\u66FF\u6362\u4E3A\u76F4\u63A5\u5F15\u7528\u7684\u8FC7\u7A0B</strong>\u3002</p><ul><li><p><code>\u7C7B\u6216\u63A5\u53E3\u7684\u89E3\u6790</code>\uFF1A\u5224\u65AD\u6240\u8981\u8F6C\u5316\u6210\u7684\u76F4\u63A5\u5F15\u7528\u662F\u6570\u7EC4\u7C7B\u578B\uFF0C\u8FD8\u662F\u666E\u901A\u7684\u5BF9\u8C61\u7C7B\u578B\u7684\u5F15\u7528\uFF0C\u4ECE\u800C\u8FDB\u884C\u4E0D\u540C\u7684\u89E3\u6790\u3002</p></li><li><p><code>\u5B57\u6BB5\u89E3\u6790</code>\uFF1A\u5BF9\u5B57\u6BB5\u8FDB\u884C\u89E3\u6790\u65F6\uFF0C\u4F1A\u5148\u5728\u672C\u7C7B\u4E2D\u67E5\u627E\u662F\u5426\u5305\u542B\u6709\u7B80\u5355\u540D\u79F0\u548C\u5B57\u6BB5\u63CF\u8FF0\u7B26\u90FD\u4E0E\u76EE\u6807\u76F8\u5339\u914D\u7684\u5B57\u6BB5\uFF0C\u5982\u679C\u6709\uFF0C\u5219\u67E5\u627E\u7ED3\u675F\uFF1B\u5982\u679C\u6CA1\u6709\uFF0C\u5219\u4F1A\u6309\u7167\u7EE7\u627F\u5173\u7CFB\u4ECE\u4E0A\u5F80\u4E0B\u9012\u5F52\u641C\u7D22\u8BE5\u7C7B\u6240\u5B9E\u73B0\u7684\u5404\u4E2A\u63A5\u53E3\u548C\u5B83\u4EEC\u7684\u7236\u63A5\u53E3\uFF0C\u8FD8\u6CA1\u6709\uFF0C\u5219\u6309\u7167\u7EE7\u627F\u5173\u7CFB\u4ECE\u4E0A\u5F80\u4E0B\u9012\u5F52\u641C\u7D22\u5176\u7236\u7C7B\uFF0C\u76F4\u81F3\u67E5\u627E\u7ED3\u675F\uFF0C</p></li></ul><h2 id="\u521D\u59CB\u5316" tabindex="-1"><a class="header-anchor" href="#\u521D\u59CB\u5316" aria-hidden="true">#</a> \u521D\u59CB\u5316</h2><p><strong>\u6240\u6709\u7684\u7C7B\u53D8\u91CF(\u5373\u9759\u6001\u91CF)\u521D\u59CB\u5316\u8BED\u53E5\u548C\u7C7B\u578B\u7684\u9759\u6001\u521D\u59CB\u5316\u5668\u90FD\u88ABJava\u7F16\u8BD1\u5668\u6536\u96C6\u5728\u4E00\u8D77\uFF0C\u653E\u5230\u4E00\u4E2A\u7279\u6B8A\u7684\u65B9\u6CD5\u4E2D\u3002</strong> \u5BF9\u4E8E\u7C7B\u6765\u8BF4\uFF0C\u8FD9\u4E2A\u65B9\u6CD5\u88AB\u79F0\u4F5C\u7C7B\u521D\u59CB\u5316\u65B9\u6CD5\uFF1B\u5BF9\u4E8E\u63A5\u53E3\u6765\u8BF4\uFF0C\u5B83\u88AB\u79F0\u4E3A\u63A5\u53E3\u521D\u59CB\u5316\u65B9\u6CD5\u3002\u5728\u7C7B\u548C\u63A5\u53E3\u7684 class \u6587\u4EF6\u4E2D\uFF0C\u8FD9\u4E2A\u65B9\u6CD5\u88AB\u79F0\u4E3A<code>&lt;clinit&gt;</code>\u3002</p><ol><li>\u5982\u679C\u5B58\u5728\u76F4\u63A5\u7236\u7C7B\uFF0C\u4E14\u76F4\u63A5\u7236\u7C7B\u6CA1\u6709\u88AB\u521D\u59CB\u5316\uFF0C\u5148\u521D\u59CB\u5316\u76F4\u63A5\u7236\u7C7B\u3002</li><li>\u5982\u679C\u7C7B\u5B58\u5728\u4E00\u4E2A\u7C7B\u521D\u59CB\u5316\u65B9\u6CD5\uFF0C\u6267\u884C\u6B64\u65B9\u6CD5\u3002</li></ol><p>\u8FD9\u4E2A\u6B65\u9AA4\u662F\u9012\u5F52\u6267\u884C\u7684\uFF0C\u5373\u7B2C\u4E00\u4E2A\u521D\u59CB\u5316\u7684\u7C7B\u4E00\u5B9A\u662F<code>Object</code>\u3002</p><p><strong>Java\u865A\u62DF\u673A\u5FC5\u987B\u786E\u4FDD\u521D\u59CB\u5316\u8FC7\u7A0B\u88AB\u6B63\u786E\u5730\u540C\u6B65\u3002</strong> \u5982\u679C\u591A\u4E2A\u7EBF\u7A0B\u9700\u8981\u521D\u59CB\u5316\u4E00\u4E2A\u7C7B\uFF0C\u4EC5\u4EC5\u5141\u8BB8\u4E00\u4E2A\u7EBF\u7A0B\u6765\u8FDB\u884C\u521D\u59CB\u5316\uFF0C\u5176\u4ED6\u7EBF\u7A0B\u9700\u7B49\u5F85\u3002</p><blockquote><p>\u8FD9\u4E2A\u7279\u6027\u53EF\u4EE5\u7528\u6765\u5199\u5355\u4F8B\u6A21\u5F0F\u3002</p></blockquote><h3 id="clinit-\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#clinit-\u65B9\u6CD5" aria-hidden="true">#</a> Clinit \u65B9\u6CD5</h3><ul><li>\u5BF9\u4E8E\u9759\u6001\u53D8\u91CF\u548C\u9759\u6001\u521D\u59CB\u5316\u8BED\u53E5\u6765\u8BF4\uFF1A\u6267\u884C\u7684\u987A\u5E8F\u548C\u5B83\u4EEC\u5728\u7C7B\u6216\u63A5\u53E3\u4E2D\u51FA\u73B0\u7684\u987A\u5E8F\u6709\u5173\u3002</li><li><strong>\u5E76\u975E\u6240\u6709\u7684\u7C7B\u90FD\u9700\u8981\u5728\u5B83\u4EEC\u7684<code>class</code>\u6587\u4EF6\u4E2D\u62E5\u6709<code>&lt;clinit&gt;()</code>\u65B9\u6CD5\uFF0C</strong> \u5982\u679C\u7C7B\u6CA1\u6709\u58F0\u660E\u4EFB\u4F55\u7C7B\u53D8\u91CF\uFF0C\u4E5F\u6CA1\u6709\u9759\u6001\u521D\u59CB\u5316\u8BED\u53E5\uFF0C\u90A3\u4E48\u5B83\u5C31\u4E0D\u4F1A\u6709<code>&lt;clinit&gt;()</code>\u65B9\u6CD5\u3002\u5982\u679C\u7C7B\u58F0\u660E\u4E86\u7C7B\u53D8\u91CF\uFF0C\u4F46\u6CA1\u6709\u660E\u786E\u7684\u4F7F\u7528\u7C7B\u53D8\u91CF\u521D\u59CB\u5316\u8BED\u53E5\u6216\u8005\u9759\u6001\u4EE3\u7801\u5757\u6765\u521D\u59CB\u5316\u5B83\u4EEC\uFF0C\u4E5F\u4E0D\u4F1A\u6709<code>&lt;clinit&gt;()</code>\u65B9\u6CD5\u3002\u5982\u679C\u7C7B\u4EC5\u5305\u542B\u9759\u6001<code>final</code>\u5E38\u91CF\u7684\u7C7B\u53D8\u91CF\u521D\u59CB\u5316\u8BED\u53E5\uFF0C\u800C\u4E14\u8FD9\u4E9B\u7C7B\u53D8\u91CF\u521D\u59CB\u5316\u8BED\u53E5\u91C7\u7528\u7F16\u8BD1\u65F6\u5E38\u91CF\u8868\u8FBE\u5F0F\uFF0C\u7C7B\u4E5F\u4E0D\u4F1A\u6709<code>&lt;clinit&gt;()</code>\u65B9\u6CD5\u3002<strong>\u53EA\u6709\u90A3\u4E9B\u9700\u8981\u6267\u884CJava\u4EE3\u7801\u6765\u8D4B\u503C\u7684\u7C7B\u624D\u4F1A\u6709<code>&lt;clinit&gt;()</code></strong></li><li><code>final</code>\u5E38\u91CF\uFF1AJava\u865A\u62DF\u673A\u5728\u4F7F\u7528\u5B83\u4EEC\u7684\u4EFB\u4F55\u7C7B\u7684\u5E38\u91CF\u6C60\u6216\u5B57\u8282\u7801\u4E2D\u76F4\u63A5\u5B58\u653E\u7684\u662F\u5B83\u4EEC\u8868\u793A\u7684\u5E38\u91CF\u503C\u3002</li></ul>',36);function r(o,i){return d}var n=t(l,[["render",r]]);export{n as default};
