import{_ as a,r as d,o as c,b as n,d as e,e as t,F as s,a as r,f as p}from"./app.f83d96fd.js";var i="/document/assets/15a2370c552e932907f8b2d3587171ef.ecd02b89.png",l="/document/assets/e4696fae4a417bdd70dd04f0786647ed.67702418.png",E="/document/assets/d2d41261df71879fab0eb54771688d78.659b684f.png",b="/document/assets/b356bfdde7d52c6993a697c4529d2f6b.e299c5e5.png",h="/document/assets/8d3094a3893ae4d1806dfcb3a93b7dff.55234afb.png";const S={},m=r('<h3 id="\u51E0\u79CD\u9694\u79BB\u7EA7\u522B" tabindex="-1"><a class="header-anchor" href="#\u51E0\u79CD\u9694\u79BB\u7EA7\u522B" aria-hidden="true">#</a> \u51E0\u79CD\u9694\u79BB\u7EA7\u522B</h3><p>\u4E8B\u52A1\u7684\u9694\u79BB\u6027\u662F\u6570\u636E\u5E93\u5904\u7406\u6570\u636E\u7684\u51E0\u5927\u57FA\u7840\u4E4B\u4E00\uFF0C\u800C\u9694\u79BB\u7EA7\u522B\u5176\u5B9E\u5C31\u662F\u63D0\u4F9B\u7ED9\u7528\u6237\u7528\u4E8E\u5728\u6027\u80FD\u548C\u53EF\u9760\u6027\u505A\u51FA\u9009\u62E9\u548C\u6743\u8861\u7684\u914D\u7F6E\u9879\u3002</p><p>ISO \u548C ANIS SQL \u6807\u51C6\u5236\u5B9A\u4E86\u56DB\u79CD\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B\uFF0C\u800C InnoDB \u9075\u5FAA\u4E86 SQL:1992 \u6807\u51C6\u4E2D\u7684\u56DB\u79CD\u9694\u79BB\u7EA7\u522B\uFF1A<code>READ UNCOMMITED</code>\u3001<code>READ COMMITED</code>\u3001<code>REPEATABLE READ</code> \u548C <code>SERIALIZABLE</code>\uFF1B\u6BCF\u4E2A\u4E8B\u52A1\u7684\u9694\u79BB\u7EA7\u522B\u5176\u5B9E\u90FD\u6BD4\u4E0A\u4E00\u7EA7\u591A\u89E3\u51B3\u4E86\u4E00\u4E2A\u95EE\u9898\uFF1A</p><ul><li><p><code>RAED UNCOMMITED</code>\uFF1A\u4F7F\u7528\u67E5\u8BE2\u8BED\u53E5\u4E0D\u4F1A\u52A0\u9501\uFF0C\u53EF\u80FD\u4F1A\u8BFB\u5230\u672A\u63D0\u4EA4\u7684\u884C\uFF08Dirty Read\uFF09\uFF1B</p><blockquote><p>\u53EF\u4EE5\u8BFB\u53D6\u672A\u63D0\u4EA4\u8BB0\u5F55\u3002\u6B64\u9694\u79BB\u7EA7\u522B\uFF0C\u4E0D\u4F1A\u4F7F\u7528\uFF0C\u5FFD\u7565\u3002</p></blockquote></li><li><p><code>READ COMMITED</code>\uFF1A\u53EA\u5BF9\u8BB0\u5F55\u52A0\u8BB0\u5F55\u9501\uFF0C\u800C\u4E0D\u4F1A\u5728\u8BB0\u5F55\u4E4B\u95F4\u52A0\u95F4\u9699\u9501\uFF0C\u6240\u4EE5\u5141\u8BB8\u65B0\u7684\u8BB0\u5F55\u63D2\u5165\u5230\u88AB\u9501\u5B9A\u8BB0\u5F55\u7684\u9644\u8FD1\uFF0C\u6240\u4EE5\u518D\u591A\u6B21\u4F7F\u7528\u67E5\u8BE2\u8BED\u53E5\u65F6\uFF0C\u53EF\u80FD\u5F97\u5230\u4E0D\u540C\u7684\u7ED3\u679C\uFF08Non-Repeatable Read\uFF09\uFF1B</p><blockquote><p>\u5FEB\u7167\u8BFB\u5FFD\u7565\uFF0C\u672C\u6587\u4E0D\u8003\u8651\u3002\u9488\u5BF9\u5F53\u524D\u8BFB\uFF0CRC\u9694\u79BB\u7EA7\u522B\u4FDD\u8BC1\u5BF9\u8BFB\u53D6\u5230\u7684\u8BB0\u5F55\u52A0\u9501 (\u8BB0\u5F55\u9501)\uFF0C\u5B58\u5728\u5E7B\u8BFB\u73B0\u8C61\u3002</p></blockquote></li><li><p><code>REPEATABLE READ</code>\uFF1A\u5FEB\u7167\u8BFB\u5FFD\u7565\uFF0C\u672C\u6587\u4E0D\u8003\u8651\u3002\u9488\u5BF9\u5F53\u524D\u8BFB\uFF0C<strong>RR\u9694\u79BB\u7EA7\u522B\u4FDD\u8BC1\u5BF9\u8BFB\u53D6\u5230\u7684\u8BB0\u5F55\u52A0\u9501 (\u8BB0\u5F55\u9501)\uFF0C\u540C\u65F6\u4FDD\u8BC1\u5BF9\u8BFB\u53D6\u7684\u8303\u56F4\u52A0\u9501\uFF0C\u65B0\u7684\u6EE1\u8DB3\u67E5\u8BE2\u6761\u4EF6\u7684\u8BB0\u5F55\u4E0D\u80FD\u591F\u63D2\u5165 (\u95F4\u9699\u9501)</strong>\uFF0C\u4E0D\u5B58\u5728\u5E7B\u8BFB\u73B0\u8C61\u3002</p></li><li><p><code>SERIALIZABLE</code>\uFF1A\u4ECEMVCC\u5E76\u53D1\u63A7\u5236\u9000\u5316\u4E3A\u57FA\u4E8E\u9501\u7684\u5E76\u53D1\u63A7\u5236\u3002\u4E0D\u533A\u522B\u5FEB\u7167\u8BFB\u4E0E\u5F53\u524D\u8BFB\uFF0C\u6240\u6709\u7684\u8BFB\u64CD\u4F5C\u5747\u4E3A\u5F53\u524D\u8BFB\uFF0C\u8BFB\u52A0\u8BFB\u9501 (S\u9501)\uFF0C\u5199\u52A0\u5199\u9501 (X\u9501)\u3002</p><blockquote><p>Serializable\u9694\u79BB\u7EA7\u522B\u4E0B\uFF0C\u8BFB\u5199\u51B2\u7A81\uFF0C\u56E0\u6B64\u5E76\u53D1\u5EA6\u6025\u5267\u4E0B\u964D\uFF0C\u5728MySQL/InnoDB\u4E0B\u4E0D\u5EFA\u8BAE\u4F7F\u7528\u3002</p></blockquote></li></ul><p>MySQL \u4E2D\u9ED8\u8BA4\u7684\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B\u5C31\u662F <code>REPEATABLE READ</code>\uFF0C\u4F46\u662F\u5B83\u901A\u8FC7 Next-Key \u9501\u4E5F\u80FD\u591F\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\u89E3\u51B3\u5E7B\u8BFB\u7684\u95EE\u9898\u3002</p><p><img src="'+i+`" alt="image"></p><p>\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u5C06\u6570\u636E\u5E93\u4E2D\u521B\u5EFA\u5982\u4E0B\u7684\u8868\u5E76\u901A\u8FC7\u4E2A\u4F8B\u5B50\u6765\u5C55\u793A\u5728\u4E0D\u540C\u7684\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B\u4E4B\u4E0B\uFF0C\u4F1A\u53D1\u751F\u4EC0\u4E48\u6837\u7684\u95EE\u9898\uFF1A</p><div class="language-text ext-text line-numbers-mode"><pre class="language-text"><code>    CREATE TABLE test(
        id INT NOT NULL,
        UNIQUE(id)
    );
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="\u810F\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u810F\u8BFB" aria-hidden="true">#</a> \u810F\u8BFB</h3><blockquote><p>\u5728\u4E00\u4E2A\u4E8B\u52A1\u4E2D\uFF0C\u8BFB\u53D6\u4E86\u5176\u4ED6\u4E8B\u52A1\u672A\u63D0\u4EA4\u7684\u6570\u636E\u3002</p></blockquote><p>\u5F53\u4E8B\u52A1\u7684\u9694\u79BB\u7EA7\u522B\u4E3A <code>READ UNCOMMITED</code> \u65F6\uFF0C\u6211\u4EEC\u5728 <code>SESSION 2</code> \u4E2D\u63D2\u5165\u7684<strong>\u672A\u63D0\u4EA4</strong>\u6570\u636E\u5728 <code>SESSION 1</code> \u4E2D\u662F\u53EF\u4EE5\u8BBF\u95EE\u7684\u3002</p><p><img src="`+l+'" alt="image"></p><h3 id="\u4E0D\u53EF\u91CD\u590D\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u53EF\u91CD\u590D\u8BFB" aria-hidden="true">#</a> \u4E0D\u53EF\u91CD\u590D\u8BFB</h3><blockquote><p>\u5728\u4E00\u4E2A\u4E8B\u52A1\u4E2D\uFF0C\u540C\u4E00\u884C\u8BB0\u5F55\u88AB\u8BBF\u95EE\u4E86\u4E24\u6B21\u5374\u5F97\u5230\u4E86\u4E0D\u540C\u7684\u7ED3\u679C\u3002</p></blockquote><p>\u5F53\u4E8B\u52A1\u7684\u9694\u79BB\u7EA7\u522B\u4E3A <code>READ COMMITED</code> \u65F6\uFF0C\u867D\u7136\u89E3\u51B3\u4E86\u810F\u8BFB\u7684\u95EE\u9898\uFF0C\u4F46\u662F\u5982\u679C\u5728 <code>SESSION 1</code> \u5148\u67E5\u8BE2\u4E86<strong>\u4E00\u884C</strong>\u6570\u636E\uFF0C\u5728\u8FD9\u4E4B\u540E <code>SESSION 2</code> \u4E2D\u4FEE\u6539\u4E86\u540C\u4E00\u884C\u6570\u636E\u5E76\u4E14\u63D0\u4EA4\u4E86\u4FEE\u6539\uFF0C\u5728\u8FD9\u65F6\uFF0C\u5982\u679C <code>SESSION 1</code> \u4E2D\u518D\u6B21\u4F7F\u7528\u76F8\u540C\u7684\u67E5\u8BE2\u8BED\u53E5\uFF0C\u5C31\u4F1A\u53D1\u73B0\u4E24\u6B21\u67E5\u8BE2\u7684\u7ED3\u679C\u4E0D\u4E00\u6837\u3002</p><p><img src="'+E+'" alt="image"></p><p>\u4E0D\u53EF\u91CD\u590D\u8BFB\u7684\u539F\u56E0\u5C31\u662F\uFF0C\u5728 <code>READ COMMITED</code> \u7684\u9694\u79BB\u7EA7\u522B\u4E0B\uFF0C\u5B58\u50A8\u5F15\u64CE\u4E0D\u4F1A\u5728\u67E5\u8BE2\u8BB0\u5F55\u65F6\u6DFB\u52A0\u884C\u9501\uFF0C\u9501\u5B9A <code>id = 3</code> \u8FD9\u6761\u8BB0\u5F55\u3002</p><h3 id="\u5E7B\u8BFB" tabindex="-1"><a class="header-anchor" href="#\u5E7B\u8BFB" aria-hidden="true">#</a> \u5E7B\u8BFB</h3><blockquote><p>\u5728\u4E00\u4E2A\u4E8B\u52A1\u4E2D\uFF0C\u540C\u4E00\u4E2A\u8303\u56F4\u5185\u7684\u8BB0\u5F55\u88AB\u8BFB\u53D6\u65F6\uFF0C\u5176\u4ED6\u4E8B\u52A1\u5411\u8FD9\u4E2A\u8303\u56F4\u6DFB\u52A0\u4E86\u65B0\u7684\u8BB0\u5F55\u3002</p></blockquote><p>\u91CD\u65B0\u5F00\u542F\u4E86\u4E24\u4E2A\u4F1A\u8BDD <code>SESSION 1</code> \u548C <code>SESSION 2</code>\uFF0C\u5728 <code>SESSION 1</code> \u4E2D\u6211\u4EEC\u67E5\u8BE2\u5168\u8868\u7684\u4FE1\u606F\uFF0C\u6CA1\u6709\u5F97\u5230\u4EFB\u4F55\u8BB0\u5F55\uFF1B\u5728 <code>SESSION 2</code> \u4E2D\u5411\u8868\u4E2D\u63D2\u5165\u4E00\u6761\u6570\u636E\u5E76\u63D0\u4EA4\uFF1B\u7531\u4E8E <code>REPEATABLE READ</code> \u7684\u539F\u56E0\uFF0C\u518D\u6B21\u67E5\u8BE2\u5168\u8868\u7684\u6570\u636E\u65F6\uFF0C\u6211\u4EEC\u83B7\u5F97\u5230\u7684\u4ECD\u7136\u662F\u7A7A\u96C6\uFF0C\u4F46\u662F\u5728\u5411\u8868\u4E2D\u63D2\u5165\u540C\u6837\u7684\u6570\u636E\u5374\u51FA\u73B0\u4E86\u9519\u8BEF\u3002</p><p><img src="'+b+'" alt="image"></p><p>\u8FD9\u79CD\u73B0\u8C61\u5728\u6570\u636E\u5E93\u4E2D\u5C31\u88AB\u79F0\u4F5C\u5E7B\u8BFB\uFF0C\u867D\u7136\u6211\u4EEC\u4F7F\u7528\u67E5\u8BE2\u8BED\u53E5\u5F97\u5230\u4E86\u4E00\u4E2A\u7A7A\u7684\u96C6\u5408\uFF0C\u4F46\u662F\u63D2\u5165\u6570\u636E\u65F6\u5374\u5F97\u5230\u4E86\u9519\u8BEF\uFF0C\u597D\u50CF\u4E4B\u524D\u7684\u67E5\u8BE2\u662F\u5E7B\u89C9\u4E00\u6837\u3002</p><p>\u5728\u6807\u51C6\u7684\u4E8B\u52A1\u9694\u79BB\u7EA7\u522B\u4E2D\uFF0C\u5E7B\u8BFB\u662F\u7531\u66F4\u9AD8\u7684\u9694\u79BB\u7EA7\u522B <code>SERIALIZABLE</code> \u89E3\u51B3\u7684\uFF0C\u4F46\u662F\u5B83\u4E5F\u53EF\u4EE5\u901A\u8FC7 MySQL \u63D0\u4F9B\u7684 <code>Next-Key</code> \u9501\u89E3\u51B3\uFF1A</p><p><img src="'+h+'" alt="image"></p><p><code>REPEATABLE READ</code> \u548C <code>READ UNCOMMITED</code> \u5176\u5B9E\u662F\u77DB\u76FE\u7684\uFF0C\u5982\u679C\u4FDD\u8BC1\u4E86\u524D\u8005\u5C31\u770B\u4E0D\u5230\u5DF2\u7ECF\u63D0\u4EA4\u7684\u4E8B\u52A1\uFF0C\u5982\u679C\u4FDD\u8BC1\u4E86\u540E\u8005\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u4E24\u6B21\u67E5\u8BE2\u7684\u7ED3\u679C\u4E0D\u540C\uFF0CMySQL \u4E3A\u6211\u4EEC\u63D0\u4F9B\u4E86\u4E00\u79CD\u6298\u4E2D\u7684\u65B9\u5F0F\uFF0C\u80FD\u591F\u5728 <code>REPEATABLE READ</code> \u6A21\u5F0F\u4E0B\u52A0\u9501\u8BBF\u95EE\u5DF2\u7ECF\u63D0\u4EA4\u7684\u6570\u636E\uFF0C\u5176\u672C\u8EAB\u5E76\u4E0D\u80FD\u89E3\u51B3\u5E7B\u8BFB\u7684\u95EE\u9898\uFF0C\u800C\u662F\u901A\u8FC7\u6587\u7AE0\u524D\u9762\u63D0\u5230\u7684 <code>Next-Key</code> \u9501\u6765\u89E3\u51B3\u3002</p><h2 id="\u51FA\u5904" tabindex="-1"><a class="header-anchor" href="#\u51FA\u5904" aria-hidden="true">#</a> \u51FA\u5904</h2>',26),u={href:"https://hadyang.com/interview/docs/basic/database/mysql/innodb/transaction/",target:"_blank",rel:"noopener noreferrer"},A=p("https://hadyang.com/interview/docs/basic/database/mysql/innodb/transaction/");function I(R,_){const o=d("ExternalLinkIcon");return c(),n(s,null,[m,e("p",null,[e("a",u,[A,t(o)])])],64)}var f=a(S,[["render",I]]);export{f as default};
