import{_ as r,r as t,o as n,b as a,d as e,e as i,F as s,a as l,f as c}from"./app.c4788b05.js";const d={},p=l('<h1 id="mq" tabindex="-1"><a class="header-anchor" href="#mq" aria-hidden="true">#</a> MQ</h1><p>\u6D88\u606F\u961F\u5217\u6280\u672F(Message Queue) \u662F\u5206\u5E03\u5F0F\u5E94\u7528\u95F4\u4EA4\u6362\u4FE1\u606F\u7684\u4E00\u79CD\u6280\u672F\u3002\u6D88\u606F\u961F\u5217\u53EF\u9A7B\u7559\u5728\u5185\u5B58\u6216\u78C1\u76D8\u4E0A, \u961F\u5217\u5B58\u50A8\u6D88\u606F\u76F4\u5230\u5B83\u4EEC\u88AB\u5E94\u7528\u7A0B\u5E8F\u8BFB\u8D70\u3002\u901A\u8FC7\u6D88\u606F\u961F\u5217\uFF0C\u5E94\u7528\u7A0B\u5E8F\u53EF\u72EC\u7ACB\u5730\u6267\u884C \u2014\u2014\u2014\u2014 \u5B83\u4EEC\u4E0D\u9700\u8981\u77E5\u9053\u5F7C\u6B64\u7684\u4F4D\u7F6E\u3001\u6216\u5728\u7EE7\u7EED\u6267\u884C\u524D\u4E0D\u9700\u8981\u7B49\u5F85\u63A5\u6536\u7A0B\u5E8F\u63A5\u6536\u6B64\u6D88\u606F\u3002\u5728\u5206\u5E03\u5F0F\u8BA1\u7B97\u73AF\u5883\u4E2D\uFF0C\u4E3A\u4E86\u96C6\u6210\u5206\u5E03\u5F0F\u5E94\u7528\uFF0C\u5F00\u53D1\u8005\u9700\u8981\u5BF9\u5F02\u6784\u7F51\u7EDC\u73AF\u5883\u4E0B\u7684\u5206\u5E03\u5F0F\u5E94\u7528\u63D0\u4F9B\u6709\u6548\u7684\u901A\u4FE1\u624B\u6BB5\u3002</p><h2 id="mq\u4F7F\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#mq\u4F7F\u7528\u573A\u666F" aria-hidden="true">#</a> MQ\u4F7F\u7528\u573A\u666F</h2><ol><li><p><strong>\u5F02\u6B65\u901A\u4FE1</strong>\uFF1A\u6709\u4E9B\u4E1A\u52A1\u4E0D\u60F3\u4E5F\u4E0D\u9700\u8981\u7ACB\u5373\u5904\u7406\u6D88\u606F\u3002\u6D88\u606F\u961F\u5217\u63D0\u4F9B\u4E86\u5F02\u6B65\u5904\u7406\u673A\u5236\uFF0C\u5141\u8BB8\u7528\u6237\u628A\u4E00\u4E2A\u6D88\u606F\u653E\u5165\u961F\u5217\uFF0C\u4F46\u5E76\u4E0D\u7ACB\u5373\u5904\u7406\u5B83\u3002\u60F3\u5411\u961F\u5217\u4E2D\u653E\u5165\u591A\u5C11\u6D88\u606F\u5C31\u653E\u591A\u5C11\uFF0C\u7136\u540E\u5728\u9700\u8981\u7684\u65F6\u5019\u518D\u53BB\u5904\u7406\u5B83\u4EEC\u3002</p></li><li><p><strong>\u89E3\u8026</strong>\uFF1A\u964D\u4F4E\u5DE5\u7A0B\u95F4\u7684\u5F3A\u4F9D\u8D56\u7A0B\u5EA6\uFF0C\u9488\u5BF9\u5F02\u6784\u7CFB\u7EDF\u8FDB\u884C\u9002\u914D\u3002\u5728\u9879\u76EE\u542F\u52A8\u4E4B\u521D\u6765\u9884\u6D4B\u5C06\u6765\u9879\u76EE\u4F1A\u78B0\u5230\u4EC0\u4E48\u9700\u6C42\uFF0C\u662F\u6781\u5176\u56F0\u96BE\u7684\u3002\u901A\u8FC7\u6D88\u606F\u7CFB\u7EDF\u5728\u5904\u7406\u8FC7\u7A0B\u4E2D\u95F4\u63D2\u5165\u4E86\u4E00\u4E2A\u9690\u542B\u7684\u3001\u57FA\u4E8E\u6570\u636E\u7684\u63A5\u53E3\u5C42\uFF0C\u4E24\u8FB9\u7684\u5904\u7406\u8FC7\u7A0B\u90FD\u8981\u5B9E\u73B0\u8FD9\u4E00\u63A5\u53E3\uFF0C\u5F53\u5E94\u7528\u53D1\u751F\u53D8\u5316\u65F6\uFF0C\u53EF\u4EE5\u72EC\u7ACB\u7684\u6269\u5C55\u6216\u4FEE\u6539\u4E24\u8FB9\u7684\u5904\u7406\u8FC7\u7A0B\uFF0C\u53EA\u8981\u786E\u4FDD\u5B83\u4EEC\u9075\u5B88\u540C\u6837\u7684\u63A5\u53E3\u7EA6\u675F</p></li><li><p><strong>\u5197\u4F59</strong>\uFF1A\u6709\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u5904\u7406\u6570\u636E\u7684\u8FC7\u7A0B\u4F1A\u5931\u8D25\u3002\u9664\u975E\u6570\u636E\u88AB\u6301\u4E45\u5316\uFF0C\u5426\u5219\u5C06\u9020\u6210\u4E22\u5931\u3002\u6D88\u606F\u961F\u5217\u628A\u6570\u636E\u8FDB\u884C\u6301\u4E45\u5316\u76F4\u5230\u5B83\u4EEC\u5DF2\u7ECF\u88AB\u5B8C\u5168\u5904\u7406\uFF0C\u901A\u8FC7\u8FD9\u4E00\u65B9\u5F0F\u89C4\u907F\u4E86\u6570\u636E\u4E22\u5931\u98CE\u9669\u3002\u8BB8\u591A\u6D88\u606F\u961F\u5217\u6240\u91C7\u7528\u7684&quot;\u63D2\u5165-\u83B7\u53D6-\u5220\u9664&quot;\u8303\u5F0F\u4E2D\uFF0C\u5728\u628A\u4E00\u4E2A\u6D88\u606F\u4ECE\u961F\u5217\u4E2D\u5220\u9664\u4E4B\u524D\uFF0C\u9700\u8981\u4F60\u7684\u5904\u7406\u7CFB\u7EDF\u660E\u786E\u7684\u6307\u51FA\u8BE5\u6D88\u606F\u5DF2\u7ECF\u88AB\u5904\u7406\u5B8C\u6BD5\uFF0C\u4ECE\u800C\u786E\u4FDD\u4F60\u7684\u6570\u636E\u88AB\u5B89\u5168\u7684\u4FDD\u5B58\u76F4\u5230\u4F60\u4F7F\u7528\u5B8C\u6BD5\u3002</p></li><li><p><strong>\u6269\u5C55\u6027</strong>\uFF1A\u56E0\u4E3A\u6D88\u606F\u961F\u5217\u89E3\u8026\u4E86\u4F60\u7684\u5904\u7406\u8FC7\u7A0B\uFF0C\u6240\u4EE5\u589E\u5927\u6D88\u606F\u5165\u961F\u548C\u5904\u7406\u7684\u9891\u7387\u662F\u5F88\u5BB9\u6613\u7684\uFF0C\u53EA\u8981\u53E6\u5916\u589E\u52A0\u5904\u7406\u8FC7\u7A0B\u5373\u53EF\u3002\u4E0D\u9700\u8981\u6539\u53D8\u4EE3\u7801\u3001\u4E0D\u9700\u8981\u8C03\u8282\u53C2\u6570\u3002\u4FBF\u4E8E\u5206\u5E03\u5F0F\u6269\u5BB9</p></li><li><p><strong>\u8FC7\u8F7D\u4FDD\u62A4</strong>\uFF1A\u5728\u8BBF\u95EE\u91CF\u5267\u589E\u7684\u60C5\u51B5\u4E0B\uFF0C\u5E94\u7528\u4ECD\u7136\u9700\u8981\u7EE7\u7EED\u53D1\u6325\u4F5C\u7528\uFF0C\u4F46\u662F\u8FD9\u6837\u7684\u7A81\u53D1\u6D41\u91CF\u65E0\u6CD5\u63D0\u53D6\u9884\u77E5\uFF1B\u5982\u679C\u4EE5\u4E3A\u4E86\u80FD\u5904\u7406\u8FD9\u7C7B\u77AC\u95F4\u5CF0\u503C\u8BBF\u95EE\u4E3A\u6807\u51C6\u6765\u6295\u5165\u8D44\u6E90\u968F\u65F6\u5F85\u547D\u65E0\u7591\u662F\u5DE8\u5927\u7684\u6D6A\u8D39\u3002\u4F7F\u7528\u6D88\u606F\u961F\u5217\u80FD\u591F\u4F7F\u5173\u952E\u7EC4\u4EF6\u9876\u4F4F\u7A81\u53D1\u7684\u8BBF\u95EE\u538B\u529B\uFF0C\u800C\u4E0D\u4F1A\u56E0\u4E3A\u7A81\u53D1\u7684\u8D85\u8D1F\u8377\u7684\u8BF7\u6C42\u800C\u5B8C\u5168\u5D29\u6E83</p></li><li><p><strong>\u53EF\u6062\u590D\u6027</strong>\uFF1A\u7CFB\u7EDF\u7684\u4E00\u90E8\u5206\u7EC4\u4EF6\u5931\u6548\u65F6\uFF0C\u4E0D\u4F1A\u5F71\u54CD\u5230\u6574\u4E2A\u7CFB\u7EDF\u3002\u6D88\u606F\u961F\u5217\u964D\u4F4E\u4E86\u8FDB\u7A0B\u95F4\u7684\u8026\u5408\u5EA6\uFF0C\u6240\u4EE5\u5373\u4F7F\u4E00\u4E2A\u5904\u7406\u6D88\u606F\u7684\u8FDB\u7A0B\u6302\u6389\uFF0C\u52A0\u5165\u961F\u5217\u4E2D\u7684\u6D88\u606F\u4ECD\u7136\u53EF\u4EE5\u5728\u7CFB\u7EDF\u6062\u590D\u540E\u88AB\u5904\u7406\u3002</p></li><li><p><strong>\u987A\u5E8F\u4FDD\u8BC1</strong>\uFF1A\u5728\u5927\u591A\u4F7F\u7528\u573A\u666F\u4E0B\uFF0C\u6570\u636E\u5904\u7406\u7684\u987A\u5E8F\u90FD\u5F88\u91CD\u8981\u3002\u5927\u90E8\u5206\u6D88\u606F\u961F\u5217\u672C\u6765\u5C31\u662F\u6392\u5E8F\u7684\uFF0C\u5E76\u4E14\u80FD\u4FDD\u8BC1\u6570\u636E\u4F1A\u6309\u7167\u7279\u5B9A\u7684\u987A\u5E8F\u6765\u5904\u7406\u3002</p></li><li><p><strong>\u7F13\u51B2</strong>\uFF1A\u5728\u4EFB\u4F55\u91CD\u8981\u7684\u7CFB\u7EDF\u4E2D\uFF0C\u90FD\u4F1A\u6709\u9700\u8981\u4E0D\u540C\u7684\u5904\u7406\u65F6\u95F4\u7684\u5143\u7D20\u3002\u6D88\u606F\u961F\u5217\u901A\u8FC7\u4E00\u4E2A\u7F13\u51B2\u5C42\u6765\u5E2E\u52A9\u4EFB\u52A1\u6700\u9AD8\u6548\u7387\u7684\u6267\u884C\uFF0C\u8BE5\u7F13\u51B2\u6709\u52A9\u4E8E\u63A7\u5236\u548C\u4F18\u5316\u6570\u636E\u6D41\u7ECF\u8FC7\u7CFB\u7EDF\u7684\u901F\u5EA6\u3002\u4EE5\u8C03\u8282\u7CFB\u7EDF\u54CD\u5E94\u65F6\u95F4\u3002</p></li><li><p><strong>\u6570\u636E\u6D41\u5904\u7406</strong>\uFF1A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4EA7\u751F\u7684\u6D77\u91CF\u6570\u636E\u6D41\uFF0C\u5982\uFF1A\u4E1A\u52A1\u65E5\u5FD7\u3001\u76D1\u63A7\u6570\u636E\u3001\u7528\u6237\u884C\u4E3A\u7B49\uFF0C\u9488\u5BF9\u8FD9\u4E9B\u6570\u636E\u6D41\u8FDB\u884C\u5B9E\u65F6\u6216\u6279\u91CF\u91C7\u96C6\u6C47\u603B\uFF0C\u7136\u540E\u8FDB\u884C\u5927\u6570\u636E\u5206\u6790\u662F\u5F53\u524D\u4E92\u8054\u7F51\u7684\u5FC5\u5907\u6280\u672F\uFF0C\u901A\u8FC7\u6D88\u606F\u961F\u5217\u5B8C\u6210\u6B64\u7C7B\u6570\u636E\u6536\u96C6\u662F\u6700\u597D\u7684\u9009\u62E9</p></li></ol><h2 id="mq\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#mq\u7F3A\u70B9" aria-hidden="true">#</a> MQ\u7F3A\u70B9</h2><ol><li><p>\u7CFB\u7EDF\u53EF\u7528\u6027\u964D\u4F4E\uFF1A\u7CFB\u7EDF\u5F15\u5165\u7684\u5916\u90E8\u4F9D\u8D56\u8D8A\u591A\uFF0C\u8D8A\u5BB9\u6613\u6302\u6389\u3002\u672C\u6765\u4F60\u5C31\u662F <code>A</code> \u7CFB\u7EDF\u8C03\u7528 <code>BCD</code> \u4E09\u4E2A\u7CFB\u7EDF\u7684\u63A5\u53E3\u5C31\u597D\u4E86\uFF0C <code>ABCD</code> \u56DB\u4E2A\u7CFB\u7EDF\u597D\u597D\u7684\uFF0C\u6CA1\u5565\u95EE\u9898\uFF0C\u4F60\u504F\u52A0\u4E2A <code>MQ</code> \u8FDB\u6765\uFF0C\u4E07\u4E00 <code>MQ</code> <code>\u6302\u4E86\u548B\u6574\uFF0CMQ</code> \u4E00\u6302\uFF0C\u6574\u5957\u7CFB\u7EDF\u5D29\u6E83\u7684\uFF0C\u4F60\u4E0D\u5C31\u5B8C\u4E86\uFF1F\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u961F\u5217\u7684\u9AD8\u53EF\u7528\u3002</p></li><li><p>\u7CFB\u7EDF\u590D\u6742\u5EA6\u63D0\u9AD8\uFF1A\u786C\u751F\u751F\u52A0\u4E2A <code>MQ</code> \u8FDB\u6765\uFF0C\u4F60\u600E\u4E48\u4FDD\u8BC1\u6D88\u606F\u6CA1\u6709\u91CD\u590D\u6D88\u8D39\uFF1F\u600E\u4E48\u5904\u7406\u6D88\u606F\u4E22\u5931\u7684\u60C5\u51B5\uFF1F\u600E\u4E48\u4FDD\u8BC1\u6D88\u606F\u4F20\u9012\u7684\u987A\u5E8F\u6027\uFF1F\u5934\u5927\u5934\u5927\uFF0C\u95EE\u9898\u4E00\u5927\u5806\uFF0C\u75DB\u82E6\u4E0D\u5DF2\u3002</p></li><li><p>\u4E00\u81F4\u6027\u95EE\u9898\uFF1A <code>A</code> \u7CFB\u7EDF\u5904\u7406\u5B8C\u4E86\u76F4\u63A5\u8FD4\u56DE\u6210\u529F\u4E86\uFF0C\u4EBA\u90FD\u4EE5\u4E3A\u4F60\u8FD9\u4E2A\u8BF7\u6C42\u5C31\u6210\u529F\u4E86\uFF1B\u4F46\u662F\u95EE\u9898\u662F\uFF0C\u8981\u662F <code>BCD</code> \u4E09\u4E2A\u7CFB\u7EDF\u90A3\u91CC\uFF0C <code>BD</code> \u4E24\u4E2A\u7CFB\u7EDF\u5199\u5E93\u6210\u529F\u4E86\uFF0C\u7ED3\u679C <code>C</code> \u7CFB\u7EDF\u5199\u5E93\u5931\u8D25\u4E86\uFF0C\u548B\u6574\uFF1F\u4F60\u8FD9\u6570\u636E\u5C31\u4E0D\u4E00\u81F4\u4E86\u3002</p></li></ol><h2 id="mq\u5E38\u7528\u534F\u8BAE" tabindex="-1"><a class="header-anchor" href="#mq\u5E38\u7528\u534F\u8BAE" aria-hidden="true">#</a> MQ\u5E38\u7528\u534F\u8BAE</h2><ul><li><p><strong>AMQP\u534F\u8BAE</strong> AMQP\u5373Advanced Message Queuing Protocol,\u4E00\u4E2A\u63D0\u4F9B\u7EDF\u4E00\u6D88\u606F\u670D\u52A1\u7684\u5E94\u7528\u5C42\u6807\u51C6\u9AD8\u7EA7\u6D88\u606F\u961F\u5217\u534F\u8BAE,\u662F\u5E94\u7528\u5C42\u534F\u8BAE\u7684\u4E00\u4E2A\u5F00\u653E\u6807\u51C6,\u4E3A\u9762\u5411\u6D88\u606F\u7684\u4E2D\u95F4\u4EF6\u8BBE\u8BA1\u3002\u57FA\u4E8E\u6B64\u534F\u8BAE\u7684\u5BA2\u6237\u7AEF\u4E0E\u6D88\u606F\u4E2D\u95F4\u4EF6\u53EF\u4F20\u9012\u6D88\u606F\uFF0C\u5E76\u4E0D\u53D7\u5BA2\u6237\u7AEF/\u4E2D\u95F4\u4EF6\u4E0D\u540C\u4EA7\u54C1\uFF0C\u4E0D\u540C\u5F00\u53D1\u8BED\u8A00\u7B49\u6761\u4EF6\u7684\u9650\u5236\u3002</p><blockquote><p>\u4F18\u70B9\uFF1A\u53EF\u9760\u3001\u901A\u7528</p></blockquote></li><li><p><strong>MQTT\u534F\u8BAE</strong> MQTT\uFF08Message Queuing Telemetry Transport\uFF0C\u6D88\u606F\u961F\u5217\u9065\u6D4B\u4F20\u8F93\uFF09\u662FIBM\u5F00\u53D1\u7684\u4E00\u4E2A\u5373\u65F6\u901A\u8BAF\u534F\u8BAE\uFF0C\u6709\u53EF\u80FD\u6210\u4E3A\u7269\u8054\u7F51\u7684\u91CD\u8981\u7EC4\u6210\u90E8\u5206\u3002\u8BE5\u534F\u8BAE\u652F\u6301\u6240\u6709\u5E73\u53F0\uFF0C\u51E0\u4E4E\u53EF\u4EE5\u628A\u6240\u6709\u8054\u7F51\u7269\u54C1\u548C\u5916\u90E8\u8FDE\u63A5\u8D77\u6765\uFF0C\u88AB\u7528\u6765\u5F53\u505A\u4F20\u611F\u5668\u548C\u81F4\u52A8\u5668\uFF08\u6BD4\u5982\u901A\u8FC7Twitter\u8BA9\u623F\u5C4B\u8054\u7F51\uFF09\u7684\u901A\u4FE1\u534F\u8BAE\u3002</p><blockquote><p>\u4F18\u70B9\uFF1A\u683C\u5F0F\u7B80\u6D01\u3001\u5360\u7528\u5E26\u5BBD\u5C0F\u3001\u79FB\u52A8\u7AEF\u901A\u4FE1\u3001PUSH\u3001\u5D4C\u5165\u5F0F\u7CFB\u7EDF</p></blockquote></li><li><p><strong>STOMP\u534F\u8BAE</strong>\xA0STOMP\uFF08Streaming Text Orientated Message Protocol\uFF09\u662F\u6D41\u6587\u672C\u5B9A\u5411\u6D88\u606F\u534F\u8BAE\uFF0C\u662F\u4E00\u79CD\u4E3AMOM(Message Oriented Middleware\uFF0C\u9762\u5411\u6D88\u606F\u7684\u4E2D\u95F4\u4EF6)\u8BBE\u8BA1\u7684\u7B80\u5355\u6587\u672C\u534F\u8BAE\u3002STOMP\u63D0\u4F9B\u4E00\u4E2A\u53EF\u4E92\u64CD\u4F5C\u7684\u8FDE\u63A5\u683C\u5F0F\uFF0C\u5141\u8BB8\u5BA2\u6237\u7AEF\u4E0E\u4EFB\u610FSTOMP\u6D88\u606F\u4EE3\u7406\uFF08Broker\uFF09\u8FDB\u884C\u4EA4\u4E92\u3002</p><blockquote><p>\u4F18\u70B9\uFF1A\u547D\u4EE4\u6A21\u5F0F\uFF08\u975Etopic/queue\u6A21\u5F0F\uFF09</p></blockquote></li><li><p><strong>XMPP\u534F\u8BAE</strong> XMPP\uFF08\u53EF\u6269\u5C55\u6D88\u606F\u5904\u7406\u73B0\u573A\u534F\u8BAE\uFF0CExtensible Messaging and Presence Protocol\uFF09\u662F\u57FA\u4E8E\u53EF\u6269\u5C55\u6807\u8BB0\u8BED\u8A00\uFF08XML\uFF09\u7684\u534F\u8BAE\uFF0C\u591A\u7528\u4E8E\u5373\u65F6\u6D88\u606F\uFF08IM\uFF09\u4EE5\u53CA\u5728\u7EBF\u73B0\u573A\u63A2\u6D4B\u3002\u9002\u7528\u4E8E\u670D\u52A1\u5668\u4E4B\u95F4\u7684\u51C6\u5373\u65F6\u64CD\u4F5C\u3002\u6838\u5FC3\u662F\u57FA\u4E8EXML\u6D41\u4F20\u8F93\uFF0C\u8FD9\u4E2A\u534F\u8BAE\u53EF\u80FD\u6700\u7EC8\u5141\u8BB8\u56E0\u7279\u7F51\u7528\u6237\u5411\u56E0\u7279\u7F51\u4E0A\u7684\u5176\u4ED6\u4EFB\u4F55\u4EBA\u53D1\u9001\u5373\u65F6\u6D88\u606F\uFF0C\u5373\u4F7F\u5176\u64CD\u4F5C\u7CFB\u7EDF\u548C\u6D4F\u89C8\u5668\u4E0D\u540C\u3002</p><blockquote><p>\u4F18\u70B9\uFF1A\u901A\u7528\u516C\u5F00\u3001\u517C\u5BB9\u6027\u5F3A\u3001\u53EF\u6269\u5C55\u3001\u5B89\u5168\u6027\u9AD8\uFF0C\u4F46XML\u7F16\u7801\u683C\u5F0F\u5360\u7528\u5E26\u5BBD\u5927</p></blockquote></li><li><p><strong>\u5176\u4ED6\u57FA\u4E8ETCP/IP\u81EA\u5B9A\u4E49\u7684\u534F\u8BAE</strong>\uFF1A\u6709\u4E9B\u7279\u6B8A\u6846\u67B6\uFF08\u5982\uFF1Aredis\u3001kafka\u3001zeroMq\u7B49\uFF09\u6839\u636E\u81EA\u8EAB\u9700\u8981\u672A\u4E25\u683C\u9075\u5FAAMQ\u89C4\u8303\uFF0C\u800C\u662F\u57FA\u4E8ETCP\\IP\u81EA\u884C\u5C01\u88C5\u4E86\u4E00\u5957\u534F\u8BAE\uFF0C\u901A\u8FC7\u7F51\u7EDCsocket\u63A5\u53E3\u8FDB\u884C\u4F20\u8F93\uFF0C\u5B9E\u73B0\u4E86MQ\u7684\u529F\u80FD\u3002</p></li></ul><h2 id="mq\u7684\u901A\u8BAF\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#mq\u7684\u901A\u8BAF\u6A21\u5F0F" aria-hidden="true">#</a> MQ\u7684\u901A\u8BAF\u6A21\u5F0F</h2><ol><li><p><strong>\u70B9\u5BF9\u70B9\u901A\u8BAF</strong>\uFF1A\u70B9\u5BF9\u70B9\u65B9\u5F0F\u662F\u6700\u4E3A\u4F20\u7EDF\u548C\u5E38\u89C1\u7684\u901A\u8BAF\u65B9\u5F0F\uFF0C\u5B83\u652F\u6301\u4E00\u5BF9\u4E00\u3001\u4E00\u5BF9\u591A\u3001\u591A\u5BF9\u591A\u3001\u591A\u5BF9\u4E00\u7B49\u591A\u79CD\u914D\u7F6E\u65B9\u5F0F\uFF0C\u652F\u6301\u6811\u72B6\u3001\u7F51\u72B6\u7B49\u591A\u79CD\u62D3\u6251\u7ED3\u6784\u3002</p></li><li><p><strong>\u591A\u70B9\u5E7F\u64AD</strong>\uFF1AMQ\u9002\u7528\u4E8E\u4E0D\u540C\u7C7B\u578B\u7684\u5E94\u7528\u3002\u5176\u4E2D\u91CD\u8981\u7684\uFF0C\u4E5F\u662F\u6B63\u5728\u53D1\u5C55\u4E2D\u7684\u662F&quot;\u591A\u70B9\u5E7F\u64AD&quot;\u5E94\u7528\uFF0C\u5373\u80FD\u591F\u5C06\u6D88\u606F\u53D1\u9001\u5230\u591A\u4E2A\u76EE\u6807\u7AD9\u70B9(Destination List)\u3002\u53EF\u4EE5\u4F7F\u7528\u4E00\u6761MQ\u6307\u4EE4\u5C06\u5355\u4E00\u6D88\u606F\u53D1\u9001\u5230\u591A\u4E2A\u76EE\u6807\u7AD9\u70B9\uFF0C\u5E76\u786E\u4FDD\u4E3A\u6BCF\u4E00\u7AD9\u70B9\u53EF\u9760\u5730\u63D0\u4F9B\u4FE1\u606F\u3002MQ\u4E0D\u4EC5\u63D0\u4F9B\u4E86\u591A\u70B9\u5E7F\u64AD\u7684\u529F\u80FD\uFF0C\u800C\u4E14\u8FD8\u62E5\u6709\u667A\u80FD\u6D88\u606F\u5206\u53D1\u529F\u80FD\uFF0C\u5728\u5C06\u4E00\u6761\u6D88\u606F\u53D1\u9001\u5230\u540C\u4E00\u7CFB\u7EDF\u4E0A\u7684\u591A\u4E2A\u7528\u6237\u65F6\uFF0CMQ\u5C06\u6D88\u606F\u7684\u4E00\u4E2A\u590D\u5236\u7248\u672C\u548C\u8BE5\u7CFB\u7EDF\u4E0A\u63A5\u6536\u8005\u7684\u540D\u5355\u53D1\u9001\u5230\u76EE\u6807MQ\u7CFB\u7EDF\u3002\u76EE\u6807MQ\u7CFB\u7EDF\u5728\u672C\u5730\u590D\u5236\u8FD9\u4E9B\u6D88\u606F\uFF0C\u5E76\u5C06\u5B83\u4EEC\u53D1\u9001\u5230\u540D\u5355\u4E0A\u7684\u961F\u5217\uFF0C\u4ECE\u800C\u5C3D\u53EF\u80FD\u51CF\u5C11\u7F51\u7EDC\u7684\u4F20\u8F93\u91CF\u3002</p></li><li><p><strong>\u53D1\u5E03/\u8BA2\u9605(Publish/Subscribe)\u6A21\u5F0F</strong>\uFF1A\u53D1\u5E03/\u8BA2\u9605\u529F\u80FD\u4F7F\u6D88\u606F\u7684\u5206\u53D1\u53EF\u4EE5\u7A81\u7834\u76EE\u7684\u961F\u5217\u5730\u7406\u6307\u5411\u7684\u9650\u5236\uFF0C\u4F7F\u6D88\u606F\u6309\u7167\u7279\u5B9A\u7684\u4E3B\u9898\u751A\u81F3\u5185\u5BB9\u8FDB\u884C\u5206\u53D1\uFF0C\u7528\u6237\u6216\u5E94\u7528\u7A0B\u5E8F\u53EF\u4EE5\u6839\u636E\u4E3B\u9898\u6216\u5185\u5BB9\u63A5\u6536\u5230\u6240\u9700\u8981\u7684\u6D88\u606F\u3002\u53D1\u5E03/\u8BA2\u9605\u529F\u80FD\u4F7F\u5F97\u53D1\u9001\u8005\u548C\u63A5\u6536\u8005\u4E4B\u95F4\u7684\u8026\u5408\u5173\u7CFB\u53D8\u5F97\u66F4\u4E3A\u677E\u6563\uFF0C\u53D1\u9001\u8005\u4E0D\u5FC5\u5173\u5FC3\u63A5\u6536\u8005\u7684\u76EE\u7684\u5730\u5740\uFF0C\u800C\u63A5\u6536\u8005\u4E5F\u4E0D\u5FC5\u5173\u5FC3\u6D88\u606F\u7684\u53D1\u9001\u5730\u5740\uFF0C\u800C\u53EA\u662F\u6839\u636E\u6D88\u606F\u7684\u4E3B\u9898\u8FDB\u884C\u6D88\u606F\u7684\u6536\u53D1\u3002\u5728MQ\u5BB6\u65CF\u4EA7\u54C1\u4E2D\uFF0CMQ Event Broker\u662F\u4E13\u95E8\u7528\u4E8E\u4F7F\u7528\u53D1\u5E03/\u8BA2\u9605\u6280\u672F\u8FDB\u884C\u6570\u636E\u901A\u8BAF\u7684\u4EA7\u54C1\uFF0C\u5B83\u652F\u6301\u57FA\u4E8E\u961F\u5217\u548C\u76F4\u63A5\u57FA\u4E8ETCP/IP\u4E24\u79CD\u65B9\u5F0F\u7684\u53D1\u5E03\u548C\u8BA2\u9605\u3002</p></li><li><p><strong>\u96C6\u7FA4(Cluster)</strong>\uFF1A\u4E3A\u4E86\u7B80\u5316\u70B9\u5BF9\u70B9\u901A\u8BAF\u6A21\u5F0F\u4E2D\u7684\u7CFB\u7EDF\u914D\u7F6E\uFF0CMQ\u63D0\u4F9B Cluster \u7684\u89E3\u51B3\u65B9\u6848\u3002\u96C6\u7FA4\u7C7B\u4F3C\u4E8E\u4E00\u4E2A \u57DF(Domain) \uFF0C\u96C6\u7FA4\u5185\u90E8\u7684\u961F\u5217\u7BA1\u7406\u5668\u4E4B\u95F4\u901A\u8BAF\u65F6\uFF0C\u4E0D\u9700\u8981\u4E24\u4E24\u4E4B\u95F4\u5EFA\u7ACB\u6D88\u606F\u901A\u9053\uFF0C\u800C\u662F\u91C7\u7528 Cluster \u901A\u9053\u4E0E\u5176\u5B83\u6210\u5458\u901A\u8BAF\uFF0C\u4ECE\u800C\u5927\u5927\u7B80\u5316\u4E86\u7CFB\u7EDF\u914D\u7F6E\u3002\u6B64\u5916\uFF0C\u96C6\u7FA4\u4E2D\u7684\u961F\u5217\u7BA1\u7406\u5668\u4E4B\u95F4\u80FD\u591F\u81EA\u52A8\u8FDB\u884C\u8D1F\u8F7D\u5747\u8861\uFF0C\u5F53\u67D0\u4E00\u961F\u5217\u7BA1\u7406\u5668\u51FA\u73B0\u6545\u969C\u65F6\uFF0C\u5176\u5B83\u961F\u5217\u7BA1\u7406\u5668\u53EF\u4EE5\u63A5\u7BA1\u5B83\u7684\u5DE5\u4F5C\uFF0C\u4ECE\u800C\u5927\u5927\u63D0\u9AD8\u7CFB\u7EDF\u7684\u9AD8\u53EF\u9760\u6027</p></li></ol><h2 id="\u6D88\u606F\u6295\u9012\u4FDD\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u6295\u9012\u4FDD\u8BC1" aria-hidden="true">#</a> \u6D88\u606F\u6295\u9012\u4FDD\u8BC1</h2><ul><li><code>At most once</code>\uFF1A\u6D88\u606F\u53EF\u80FD\u4F1A\u4E22\uFF0C\u4F46\u7EDD\u4E0D\u4F1A\u91CD\u590D\u6295\u9012</li><li><code>At least one</code>\uFF1A\u6D88\u606F\u7EDD\u4E0D\u4F1A\u4E22\uFF0C\u4F46\u53EF\u80FD\u4F1A\u91CD\u590D\u6295\u9012</li><li><code>Exactly once</code>\uFF1A\u6BCF\u6761\u6D88\u606F\u80AF\u5B9A\u4F1A\u88AB\u6295\u9012\u4E00\u6B21\u4E14\u4EC5\u6295\u9012\u4E00\u6B21\uFF0C\u5F88\u591A\u65F6\u5019\u8FD9\u662F\u7528\u6237\u6240\u60F3\u8981\u7684\u3002</li></ul><h2 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a> \u53C2\u8003\u94FE\u63A5</h2>',13),h={href:"https://github.com/doocs/advanced-java/blob/master/docs/high-concurrency/mq-interview.md",target:"_blank",rel:"noopener noreferrer"},g=c("\u6D88\u606F\u961F\u5217\u9762\u8BD5\u573A\u666F");function M(u,m){const o=t("ExternalLinkIcon");return n(),a(s,null,[p,e("p",null,[e("a",h,[g,i(o)])])],64)}var q=r(d,[["render",M]]);export{q as default};
